// صفحات منتجات مخزنة طرفياً
export const revalidate = 3600 // ساعة واحدة

export async function generateStaticParams() {
  const products = await prisma.product.findMany({
    select: { slug: true }
  })
  return products.map(p => ({ slug: p.slug }))
}
