// Dynamic image optimization
import { getOptimizedImage } from '@/lib/images'

const ProductImage = ({ src, alt, priority }) => {
  return (
    <Image
      src={getOptimizedImage(src, { width: 1200, quality: 95 })}
      alt={alt}
      width={1200}
      height={1200}
      priority={priority}
      sizes="(max-width: 768px) 100vw, 50vw"
    />
  )
}

